import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [userData, setUserData] = useState({
    fullName: '',
    username: '',
    email: '',
    password: ''
  });

  const [userId, setUserId] = useState(null); // State to store the user ID
  const [savedUsers, setSavedUsers] = useState([]); // State to store saved user data array
  const [updateStatus, setUpdateStatus] = useState(''); 
  const [isUpdateFormOpen, setIsUpdateFormOpen] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSave = async () => {
    try {
      const response = await fetch('http://localhost:5000/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(userData)
      });

      const data = await response.json();
      console.log('User saved:', data);

      // Set the user ID and saved user data to the array
      setUserId(data._id);
      setSavedUsers([...savedUsers, data]);

      // Reset the form fields
      setUserData({
        fullName: '',
        username: '',
        email: '',
        password: ''
      });

      // Clear the update status
      setUpdateStatus('');
    } catch (error) {
      console.error('Error saving user:', error);
    }
  };

  const handleUpdate = async () => {
    try {
      if (!userId) {
        console.error('User ID not available.');
        return;
      }

      const response = await fetch(`http://localhost:5000/user/${userId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(userData)
      });

      const data = await response.json();
      console.log('User updated:', data);

      // Update the update status
      setUpdateStatus('User updated successfully');

      // Clear the saved user data
      setSavedUsers(savedUsers.map((user) => (user._id === userId ? data : user)));

      // Close the update form
      setIsUpdateFormOpen(false);
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  const handleUpdateUser = (user) => {
    setUserData({
      fullName: user.fullName,
      username: user.username,
      email: user.email,
      password: user.password
    });

    setUserId(user._id);
    setIsUpdateFormOpen(true);
  };

  return (
    <div className="container">
      <h1>User Management</h1>
      <div className="form-group">
        <label>Full Name:</label>
        <input type="text" name="fullName" value={userData.fullName} onChange={handleChange} />
      </div>
      <div className="form-group">
        <label>Username:</label>
        <input type="text" name="username" value={userData.username} onChange={handleChange} />
      </div>
      <div className="form-group">
        <label>Email:</label>
        <input type="text" name="email" value={userData.email} onChange={handleChange} />
      </div>
      <div className="form-group">
        <label>Password:</label>
        <input type="text" name="password" value={userData.password} onChange={handleChange} />
      </div>

      <button className="btn" onClick={handleSave}>
        Save User
      </button>
      <button className="btn btn2" onClick={handleUpdate}>
        Update User
      </button>

      {/* Display saved user data */}
      {savedUsers.length > 0 && (
        <div className="saved-users">
          <h2>Saved Users</h2>
          {savedUsers.map((user, index) => (
            <div key={index} className="user-post">
              <p>Full Name: {user.fullName}</p>
              <p>Username: {user.username}</p>
              <p>Email: {user.email}</p>

              <button className="btn btn-update" onClick={() => handleUpdateUser(user)}>
                Update User
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Display update status */}
      {updateStatus && (
        <div className="update-status">
          <h2>Update Status</h2>
          <p>{updateStatus}</p>
        </div>
      )}


      {isUpdateFormOpen && (
        <div className="update-form">
          <h2>Update User</h2>
          <div className="form-group">
            <label>Full Name:</label>
            <input
              type="text"
              name="fullName"
              value={userData.fullName}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Username:</label>
            <input
              type="text"
              name="username"
              value={userData.username}
              onChange={handleChange}
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input type="text" name="email" value={userData.email} onChange={handleChange} />
          </div>

          <button className="btn" onClick={handleUpdate}>
            Update User
          </button>
          <button className="btn btn-cancel" onClick={() => setIsUpdateFormOpen(false)}>
            Cancel
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
